mapper.select:
SELECT r.id, r.apply_id, r.username, r.demp_id as applyDempId, DATE_FORMAT(r.apply_date,"%Y-%m-%d") AS applyDateStr, c.name AS companyName, r.state, r.result FROM flow_recruit r inner JOIN t_user u ON u.id = r.apply_id
and u.companyid = #{companyId}
LEFT JOIN t_company c ON c.id = r.company_id WHERE 1 = 1
and r.apply_id = #{applyId}
and r.username LIKE CONCAT('%',#{username},'%')
ORDER BY r.apply_date DESC
